// 从 msg.payload 中提取 variableName 和 dataType
var variableName = msg.payload.variableName;
var dataType = msg.payload.dataType;
var variableRange = msg.payload.variableRange;

var rangePattern = /^\d+(\.\d+)?-\d+(\.\d+)?$/; // 正则表达式匹配数字范围格式，如 10-20 或 10.5-20.3
if (!rangePattern.test(variableRange)) {
    // 格式不正确时返回 400 状态码
    msg.statusCode = 400;  // 设置 HTTP 状态码
    msg.payload = { 
        error: `Invalid range format: ${variableRange}. Expected format: 'number-number'.`
    };  // 返回错误信息
    return msg;  // 直接返回错误消息，不继续执行后续节点
}

let valArray = flow.get('valArray') || [];

// 检查 valArray 中是否已经存在相同 variableName 的元素
let index = valArray.findIndex(item => item.variableName === variableName);

if (index !== -1) {
    // 如果找到相同的 variableName，覆盖该元素
    valArray[index] = {
        variableName: variableName,
        dataType: dataType,
        variableRange: variableRange
    };
    node.warn("重复元素已覆盖");
} else {
    // 如果没有找到相同的 variableName，添加新元素
    const newObject = {
        variableName: variableName,
        dataType: dataType,
        variableRange: variableRange
    };
    valArray.push(newObject);
}

// 将更新后的数组存回 flow 变量
flow.set('valArray', valArray);
node.warn(valArray);

msg.statusCode = 200;
msg.payload = {
    "variableName": `${variableName}`,
    "dataType": `${dataType}`
}
// 返回消息
return msg;

flow.set('valArray', []);

// 获取现有的 flow 数据，若没有则初始化为空数组
let valArray = flow.get('valArray') || [];

// 创建新的对象
const newObject = {
    "variableName": "realTimePower",
    "dataType": "Float",
    "variableRange": "20-100"
};

const newObject2 = {
    "variableName": "irradiance",
    "dataType": "Float",
    "variableRange": "0-500"
};

const newObject3 = {
    "variableName": "operationStatus",
    "dataType": "Float",
    "variableRange": "0-1"
};

// 将新对象添加到数组中
valArray.push(newObject);
valArray.push(newObject2);
valArray.push(newObject3);

// 将更新后的数组存回 flow 变量
flow.set('valArray', valArray);
node.warn(valArray);
// 返回消息
return msg;

// 获取现有的 flow 数据，如果没有则初始化为空数组
let valArray = flow.get('valArray') || [];

// 如果 valArray 数组为空，直接返回当前的 msg
if (valArray.length === 0) {
    return msg;  // 直接返回 msg，终止后续处理
}
// 定义 variables 数组
let variables = valArray.map(item => {
    // 生成一个随机值，根据 variableRange 生成一个范围
    let minVariableRange = Math.min(...item.variableRange.split('-').map(Number)); // 获取 variableRange 范围的最小值
    let maxVariableRange = Math.max(...item.variableRange.split('-').map(Number)); // 获取 variableRange 范围的最大值
    // let randomValue = (Math.random() * (maxVariableRange - minVariableRange) + minVariableRange).toFixed(2); // 在范围内生成随机值

    // 判断范围是否是 0 到 1
    let randomValue;
    if (minVariableRange === 0 && maxVariableRange === 1) {
        // 如果范围是 0 到 1，直接返回 0 或 1
        randomValue = Math.random() < 0.5 ? 0 : 1;
    } else {
        // 否则，按常规方式生成随机浮动值
        randomValue = (Math.random() * (maxVariableRange - minVariableRange) + minVariableRange).toFixed(2);
    }


    // 返回每个元素的新结构
    return {
        name: item.variableName,
        value: randomValue,
        dataType: item.dataType
    };
});

// 创建多个 msg 对象
let messages = variables.map(variable => {
    let msg = {
        payload: {
            messageType: "Variable",
            namespace: 1,
            variableName: variable.name,
            variableValue: variable.value,
            dataType: variable.dataType
        }
    };
    return msg;
});

// 返回多个消息
return [messages];

