// 获取现有的 flow 数据，如果没有则初始化为空数组
let valArray = flow.get('valArray') || [];
node.warn(valArray);
return msg;

// 可选：设置 NodeId 等其他参数
msg.payload = {
    opcuaCommand: "restartOPCUAServer"
}
