let sql = '';
let tabName = flow.get("tabName");
let type = msg.type;

if (type == 1) {
    sql = "SELECT DATE(\"submitTime\") AS date, COUNT(*) AS orderCount, SUM(skucount) AS skuCount FROM \"" + tabName + "\" GROUP BY DATE(\"submitTime\") ORDER BY date"
} else if (type == 2) {
    sql = "SELECT customer , COUNT(*) AS total_orders, SUM(skuCount) AS total_sku_count FROM \"" + tabName + "\" GROUP BY customer ORDER BY total_orders DESC"
} else if (type == 3) {
    sql = "SELECT TO_CHAR(\"submitTime\", 'YYYY-MM') AS date, COUNT(*) AS orderCount, SUM(CASE WHEN \"userMaterialStatus\" = 0 THEN 1 ELSE 0 END) AS userMaterialCount FROM \"" + tabName + "\" GROUP BY date ORDER BY date DESC"
} else if (type == 4) {
    sql = "SELECT COUNT(*) AS total_orders, SUM(CASE WHEN status = 1 THEN 1 ELSE 0 END) AS in_transit_orders, ROUND(CAST(RANDOM() * 100 AS NUMERIC), 2) AS total_production_progress, SUM(CASE WHEN status = 1 THEN skucount ELSE 0 END) AS sku_in_production, ROUND(CAST(RANDOM() * 100 AS NUMERIC), 2) AS total_storage_progress, ROUND(100.0 * SUM(CASE WHEN \"userMaterialStatus\" = 0 THEN 1 ELSE 0 END) / COUNT(*), 2) AS unclaimed_material_ratio FROM \"" + tabName + "\""
} else if (type == 5) {
    sql = "SELECT TO_CHAR(\"submitTime\", 'YYYY-MM') AS month, COUNT(*) AS total_orders, SUM(\"receivableAmount\") AS total_receivable, SUM(\"collectedAmount\") AS total_collected, SUM(\"overdueAmount\") AS total_overdue FROM \"" + tabName + "\" GROUP BY month ORDER BY month"
}

msg.query = sql;

return msg;

